import React from 'react';
import { Container, Row, Col , Form} from 'reactstrap';
import { useParams } from 'react-router-dom'; // Import useParams from react-router-dom
import tourData from '../assets/data/tours';
import calculateAvgRating from '../utils/avgRating';
import '../style/tour-details.css'

const TourDetails = () => {
  const { id } = useParams(); // Correct the usage of useParams()

  // This is your static data; in a real app, you'd fetch this from an API or database
  const tour = tourData.find((tour) => tour.id === id);

  // Destructuring properties from the tour object
  const { photo, title, desc, price,address, reviews, city, distance, maxGroupSize } = tour;

  const {totalRating , avgRating} = calculateAvgRating(reviews)
  return (
    <>
      <section>
        <Container>
          <Row>
            <Col lg="8">
              <div className="tour_content"> {/* Fix the classname to className */}
                <img src={photo} alt={title} />

                <div className="tour_info">
                  <h2>{title}</h2>

                  <div className="tour_details">
                   <span className="tour_rating d-flex align-items-center gap-1">
                      <i className="ri-star-s-fill" style={{ color: "var(--secondary-color)" }}></i> {calculateAvgRating === 0 ? null : avgRating}
                   {totalRating === 0 ? "Not rated" : null} <span>({reviews?.length})</span>
                    </span>
                  <span>
                     <i className="ri-map-pin-user-fill"></i> {address}
                  </span>
                 </div>
                 <div className="tour_extra-details">
                  <span><i class="ri-map-pin-2-line"></i> {city}</span>
                  <span><i class="ri-money-dollar-circle-line"></i> ${price} / per person</span>
                  <span><i class="ri-group-line"></i> {maxGroupSize}</span>
                 </div>
                 <h5>Description</h5>
                 <p>{desc}</p>
              </div>
              {/*=========== tour reviews section============*/}
              <div className="tour_review mt-4">
                <h4>Reviews ({reviews?.length} reviews)</h4>

                <Form>
                  <div className="d-flex align-items-center gap-3 mb-4
                  rating_group">
                    <span><i class="ri-star-s-fill"></i></span>
                    <span><i class="ri-star-s-fill"></i></span>
                    <span><i class="ri-star-s-fill"></i></span>
                    <span><i class="ri-star-s-fill"></i></span>
                    <span><i class="ri-star-s-fill"></i></span>
                  </div>
                </Form>
              </div>
              {/*=========== tour reviews section end============*/}
            </div>

          </Col>
        </Row>
      </Container>
    </section>
    </>
  )
}

export default TourDetails;
