// ItineraryGenerator.js
import React, { useRef, useState } from "react";
import { Col, Form, FormGroup } from "reactstrap";
import axios from 'axios';
import { BASE_URL } from "../utils/config";
import '../style/itineararygenerator.css';

const ItineraryGenerator = () => {
  const sourceRef = useRef("");
  const destinationRef = useRef("");
  const startDateRef = useRef("");
  const endDateRef = useRef("");
  const durationRef = useRef("");
  const [itinerary, setItinerary] = useState(null);

  const generateItinerary = async () => {
    const source = sourceRef.current.value;
    const destination = destinationRef.current.value;
    const startDate = startDateRef.current.value;
    const endDate = endDateRef.current.value;
    const durationInDays = durationRef.current.value;

    if (source === "" || destination === "" || startDate === "" || endDate === "" || durationInDays === "") {
      return alert("All fields are required!");
    }

    try {
      const response = await axios.post(`${BASE_URL}/Itinerary/generate`, {
        source,
        destination,
        startDate,
        endDate,
        durationInDays
      });

      setItinerary(response.data.itineraryText);
    } catch (error) {
      console.error('Error generating itinerary:', error);
      alert('Error generating itinerary. Please try again.');
    }
  };

  return (
    <Col lg="12">
      <div className="itinerary_generator">
        <h2>Itinerary Generator</h2>
        <Form className="d-flex align-items-center gap-4">
          {/* Form inputs */}
          {/* ... */}
          <span className="search_icon" type="submit" onClick={generateItinerary}>
            Generate
          </span>
        </Form>
      </div>
      {/* Render itinerary */}
      <div className="itinerary">
        {itinerary && (
          <div>
            {itinerary.split('\n\n\n').map((day, index) => (
              <div className="day_box" key={index}>
                {day.trim().split('\n').map((line, index) => (
                  <div key={index}>
                    {line.includes('Day') ? (
                      <div className="day_heading">{line}</div>
                    ) : (
                      <div className="day_details">{line}</div>
                    )}
                  </div>
                ))}
              </div>
            ))}
            <div className="summary_box">Summary: {itinerary.split('Summary:')[1]}</div>
          </div>
        )}
      </div>
    </Col>
  );
};

export default ItineraryGenerator;
